mapper.insert:
/* com.ddit.proj.mapper.ScheduleMapper.insertRecordApply */
INSERT INTO schedule (
	sch_code
	, mem_no
	, sch_ttl
	, sch_dt
	, sch_st
	, sch_et
	, code_sch_se
	, code_sch_mem
) VALUES (
	TO_CHAR(
		(SELECT MAX(REGEXP_REPLACE(sch_code, '[^0-9]', ''))
		FROM schedule)+1
 	) /* 숫자 이외의 문자를 제거한 뒤 NUMBER로 형변환 후, sch_code 중 MAX 값을 문자로 저장 */
	, #{memNo}
	, #{schTtl}
	, SYSDATE
	, TO_DATE(#{schSt})
	, TO_DATE(#{schEt})
	, #{codeSchSe}
	, #{codeSchMem}
)
